/*
*
*	@Create Icon Sections
*
*
*/
function mixAll( iconClass , cssContent )
{	
	var allIcons = [];
	var allObj = {};
	var Arraylenght = iconClass.length;
	
	var keys = Object.keys(sections);

	for(var i = 0; i < Arraylenght ; i++)
	{
		var name = iconClass[i];

		var iconObj = {
			className: iconClass[i],
			cssContent:cssContent[i]
		};
		
		for(var j = 0; j < keys.length; j++)
		{
			if(name.indexOf(keys[j]) !== -1)
			{
				sections[keys[j]].push(iconObj)
			}
		}
	}
	console.log(sections);
	printToDOM(sections);
};
/*
*
*
*	@Print Icons to DOM	
*
*
*/
function printToDOM( data )
{
	var holder = $('#iconHolder');
	for(var i in data)
	{	
		var column = ''
		for(var j = 0 ; j < data[i].length; j++)
		{

			column += '<div class="col-xs-6 col-sm-3">'+
							'<div class="icon" data-className="'+data[i][j].className+'" data-cssContent="'+data[i][j].cssContent+'">'+
								'<i class="'+data[i][j].className+'"></i>&nbsp;'+
								'<small>'+data[i][j].className+'</small>'+
							'</div>'+
					  '</div>';
		}
		var row = 	'<div class="row">'+
						'<h3 style="text-transform:capitalize;">'+i+'</h3>'+
						'<hr>'+
						'<div class="iconHolder">'+column+'</div>'+
					'</div>';
		holder.append(row);
	}
}
/*
*
*	@Filter
*
*/
function filter(element) 
{
        
	var value = $(element).val();

	$(".icon").each(function() {
		if ($(this).find('small').search(value) > -1) 
		{
			$(this).parent().show();
		}
		else 
		{
			$(this).parent().hide();
		}
	});
}
/*
*
*
*	@Jquery Ready
*
*
*/
$(document).ready(function(){

	mixAll( ICONCLASS , CSSMAP );

	$('.icon').click(function(e){
		e.preventDefault();

		var cssContent 	= $(this).attr('data-cssContent');
		var className 	= $(this).attr('data-className');

		$('#selection  h5').html(className);
		$('#selection  i').attr('class', className);
		$('#selection  #css').html(cssContent);
		$('#selection  #class').html(className);
		$('#myModal').modal('show');

	});

});